        this.state = {
            name: '',
            email: '',
            phone: '',
            address: '',
            purpose: '',
            items: []
        }
    }

    componentDidMount() {
        this.getAll()
    }
    getAll = () => {
        getVisitorList().then(data => {
          this.setState(
            {
              name: '',
              items: [...data]
            },
            () => {
              console.log(this.state.items)
