import React, { Component } from 'react'
import axios from 'axios';
class EditVisitor extends Component {
  constructor(props) {
    super(props);
    this.setName = this.setName.bind(this)
    this.setEmail = this.setEmail.bind(this)
    this.setPhone = this.setPhone.bind(this)
    this.setAddress = this.setAddress.bind(this)
    this.setPurpose = this.setPurpose.bind(this)
    this.onSubmit = this.onSubmit.bind(this)

    this.state = {
      name: '',
      email: '',
      phone: '',
      address: '',
      purpose: '',
      errors: {}
    }
  }
  componentDidMount() {
    axios.get('visitors'+this.props.match.params.id)
        .then(response => {
            this.setState({ 
              person_name: response.data.person_name, 
              business_name: response.data.business_name,
              business_gst_number: response.data.business_gst_number });
        })
        .catch(function (error) {
            console.log(error);
        })
  }

  setName(e) {
    this.setState({
      name: e.target.value
    });
  }
  setEmail(e){
      this.setState({
          email: e.target.value
      })
  }
  setPhone(e){
    this.setState({
        phone: e.target.value
    })
  }
  setAddress(e){
    this.setState({
        address: e.target.value
    })
  }
  setPurpose(e){
    this.setState({
        purpose: e.target.value
    })
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-md-6 mt-5 mx-auto">
            <form noValidate onSubmit={this.onSubmit}>
              <h1 className="h2 mb-3 font-weight-normal" style={{textAlign: "center"}}>Visitor Editing Form</h1>
              <div className="form-group">
                <label htmlFor="name">Name</label>
                <input
                  type="text"
                  className="form-control"
                  name="name"
                  placeholder="Enter Your Full Name"
                  value={this.state.name}
                  onChange={this.setName}
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email address</label>
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  placeholder="Enter email"
                  value={this.state.email}
                  onChange={this.setEmail}
                />
              </div>
              <div className="form-group">
                <label htmlFor="phone">Phone Number</label>
                <input
                  type="phone"
                  className="form-control"
                  name="phone"
                  placeholder="Enter Personal Phone"
                  value={this.state.phone}
                  onChange={this.setPhone}
                />
              </div>
              <div className="form-group">
                <label htmlFor="phone">Address</label>
                <input
                  type="text"
                  className="form-control"
                  name="address"
                  placeholder="Enter Your Address"
                  value={this.state.address}
                  onChange={this.setAddress}
                />
              </div>
              <div className="form-group">
                <label htmlFor="phone">Purpose</label>
                <input
                  type="text"
                  className="form-control"
                  name="purpose"
                  placeholder="Enter Your Purpose of Visit"
                  value={this.state.purpose}
                  onChange={this.setPurpose}
                />
              </div>
              <button
                type="submit"
                className="btn btn-lg btn-primary btn-block"
              >
               Update Visitor
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default EditVisitor
