import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import EditVisitor from './EditVisitor'
import {getVisitorData, deleteVisitor, getVisitorList} from './VisitorListFunctions'

class AllVisitor extends Component{
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            address: '',
            purpose: '',
            items: [],
            editItems: []
        }
//        this.onClick = this.onClick.bind(this)
    }
    componentDidMount() {
        this.getAll()
    }
    getAll = () => {
        getVisitorList().then(data => {
          this.setState(
            {
              items: [...data]
            },
            () => {
              console.log(this.state.items)
            }
          )
        })
    }
    onEdit = (id, e) =>{
        e.preventDefault()
        getVisitorData(id).then(data => {
            this.setState(
                {
                    editItems: [...data]
                }
            )
        })
    }
    onDelete = (id, e) => {
        e.preventDefault()
        deleteVisitor(id).then(() => {
          this.getAll()
        })
    }
    render() {
        return(
            <div className="container">
                <div className="row mt-5">
                    <table className="table table-hover table-dark ">
                        <thead>  
                            <tr className="bg-primary">
                                <th scope="col">Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">Phone</th>
                                <th scope="col">Address</th>
                                <th scope="col">Purpose</th>
                                <th scope="col">Edit</th>
                                <th scope="col">Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.items.map((item, index) => (
                            <tr key={index}>
                                <td className="text-left">{item.name}</td>
                                <td className="text-left">{item.email}</td>
                                <td className="text-left">+880{item.phone}</td>
                                <td className="text-left">{item.address}</td>
                                <td className="text-left">{item.purpose}</td>
                                {/* <td><Link to={"/edit_visitor"} className="btn btn-primary">Edit</Link></td> */}
                                <td><button onClick={this.onEdit.bind(this, item.id)} className="btn btn-primary">Edit</button></td>
                                <td><button onClick={this.onDelete.bind(this, item.id)} className="btn btn-warning">Delete</button></td>
                            </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        )

    }

}

export default AllVisitor
