import React, { Component } from 'react'
import {updateVisitor,deleteVisitor, getVisitorList} from './VisitorListFunctions'

class AllVisitor extends Component{
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            address: '',
            purpose: '',
            items: []
        }
        this.onClick = this.onClick.bind(this)
    }

    componentDidMount() {
        this.getAll()
    }
    getAll = () => {
        getVisitorList().then(data => {
          this.setState(
            {
              items: [...data]
            },
            () => {
              console.log(this.state.items)
            }
          )
        })
    }

    onDelete = (id) => {
        deleteVisitor(id).then(() => {
          this.getAll()
        })
      }

    render() {
        return(
            <div className="container">
                <div className="row mt-5">
                    <table className="table table-hover table-dark ">
                        <thead>  
                            <tr className="bg-primary">
                                <th scope="col">Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">Phone</th>
                                <th scope="col">Address</th>
                                <th scope="col">Purpose</th>
                                <th scope="col">Edit</th>
                                <th scope="col">Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.items.map((item, index) => (
                            <tr key={index}>
                                <td className="text-left">{item.name}</td>
                                <td className="text-left">{item.email}</td>
                                <td className="text-left">+880{item.phone}</td>
                                <td className="text-left">{item.address}</td>
                                <td className="text-left">{item.purpose}</td>
                                <td><button onClick= {() => updateVisitor(item.id)} className="btn btn-info mr-1">Edit</button></td>
                                <td><button onClick= {this.onDelete.bind(item.id)} className="btn btn-danger">Delete</button></td>
                            </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        )

    }

}

export default AllVisitor
