import React, { Component } from 'react'
import {getVisitorList, deleteVisitor} from './VisitorListFunctions'

class AllVisitor extends Component{
    constructor() {
        super()
        this.state = {
            name: '',
            email: '',
            phone: '',
            address: '',
            purpose: '',
            items: []
        }
    }

    componentDidMount() {
        this.getAll()
    }
    getAll = () => {
        getVisitorList().then(data => {
          this.setState(
            {
              name: '',
              items: [...data]
            },
            () => {
              console.log(this.state.items)
            }
          )
        })
    }
    render() {
        return(
            <div className="container">
                <div className="row mt-5">
                    <table className="table table-hover table-dark ">
                        <thead>  
                            <tr className="bg-primary">
                                <th scope="col">Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">Phone</th>
                                <th scope="col">Address</th>
                                <th scope="col">Purpose</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.items.map((item, index) => (
                            <tr key={index}>
                                <td className="text-left">{item.name}</td>
                                <td className="text-right">
                                <button
                                    href=""
                                    className="btn btn-info mr-1"
                                    disabled={this.state.editDisabled}
                                    onClick={this.onEdit.bind(this, item.name, item.id)}
                                >
                                    Edit
                                </button>
                                <button
                                    href=""
                                    className="btn btn-danger"
                                    onClick={this.onDelete.bind(this, item.id)}
                                >
                                    Delete
                                </button>
                                </td>
                            </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        )

    }

}

export default AllVisitor
